import { useReducer } from "react";
import toDoContext from "./toDoContext";


const defaultState = {
  mainTaskArray: taskArray,
  completed: [],
  active: [],
};

const addTaskReducer = (state, action) => {

  let taskArray;
  let completedArray;
  let activeArray;

  if (action.type === 'ADD__TASK') {
    taskArray = state.concat(action.task);

    return {
      mainTaskArray: taskArray,
      completed: [],
      active: [],
    };
  }

  if (action.type === 'DELETE__TASK') {
    taskArray = state.filter(task => task.id !== action.id);

    return {
      mainTaskArray: taskArray,
      completed: [],
      active: [],
    };
  }

  if (action.type === 'COMPLETED__TASK') {
    console.log(action.id)

    const modifiedArray = state.map(task => {
      if (task.id === action.id) {
        task.isCompleted = true;
      }
      return task;
    })


    completedArray = modifiedArray.filter(item => item.isCompleted === true);

    activeArray = modifiedArray.filter(item => item.isCompleted === false);
    return {
      mainTaskArray: state,
      completed: completedArray,
      active: activeArray,
    };
  }

  return defaultState;
}

const ContextProvider = props => {

  const [taskReducer, dispatchTaskReducer] = useReducer(addTaskReducer, defaultState);


  const AddTaskHandler = (taskObject) => {

    dispatchTaskReducer({type: 'ADD__TASK', task: taskObject})
  }

  const deleteTaskHandler = (id) => {
    dispatchTaskReducer({type: 'DELETE__TASK', id: id})
  }

  const completedTask = (id) => {
    dispatchTaskReducer({type: 'COMPLETED__TASK', id: id})
  }


  const toDoContextArray =  ({
    task: taskReducer.mainTaskArray,
    taskActive: taskReducer.active,
    taskCompleted: taskReducer.completed,
    addTask: AddTaskHandler,
    deleteTask: deleteTaskHandler,
    completedTask: completedTask,
  })


  return <toDoContext.Provider value={toDoContextArray}>
    {props.children}
  </toDoContext.Provider>
}

export default ContextProvider;
